    def create_image(self):
        screen = pygame.Surface((660, 473), pygame.SRCALPHA, 32)
        x = 0
        y = 0
        for i in self.level:
            if i == 0:
                pygame.draw.rect(screen, pygame.Color('Grey'), pygame.Rect(x, y, 10, 10))
            x += 11
            if x == 660:
                print('660')
                y += 11
                x = 0
        pygame.image.save(screen, 'Images/gui.png')

    def draw_level(self, surf):
        surf.blit(self.gui_image, (0, 0))

    def write_txt(self):
        file = open('level.txt', 'w')

        for i in range(len(self.level)):
            file.write(str(self.level[i - 1]))
            if i % 60 == 0 and i != 0 and i != 1:
                file.write('\n')
        file.close()
